# Cerca Global Layouts in modi alternativi
import os
try:
    result = {}

    # 1. Verifica directory del progetto per file .lay
    project_path = r'H:\go\reports\Input\S000009result.ver'
    project_dir = os.path.dirname(project_path)

    lay_files = []
    if os.path.exists(project_dir):
        for file in os.listdir(project_dir):
            if file.endswith('.lay'):
                lay_files.append({
                    'filename': file,
                    'path': os.path.join(project_dir, file),
                    'size': os.path.getsize(os.path.join(project_dir, file))
                })

    result['lay_files_in_directory'] = lay_files

    # 2. Cerca attraverso Visum.IO se c'è un modo di listare layouts
    try:
        # Prova a vedere se c'è un attributo nascosto
        if hasattr(Visum, 'GetAttValue'):
            try:
                layout_info = Visum.GetAttValue('GlobalLayoutFile')
                result['visum_global_layout_file'] = layout_info
            except:
                result['visum_global_layout_file'] = 'Attributo non disponibile'
    except:
        pass

    # 3. Verifica se esiste un modo alternativo via Graphics
    try:
        if hasattr(Visum, 'Graphics'):
            graphics = Visum.Graphics
            result['has_graphics'] = True
            result['graphics_type'] = str(type(graphics))
        else:
            result['has_graphics'] = False
    except Exception as e:
        result['graphics_error'] = str(e)

    # 4. Cerca 'tabelle_report.lay'
    tabelle_report_path = os.path.join(project_dir, 'tabelle_report.lay')
    result['tabelle_report_exists'] = os.path.exists(tabelle_report_path)
    if os.path.exists(tabelle_report_path):
        result['tabelle_report_size'] = os.path.getsize(tabelle_report_path)

except Exception as e:
    result = {'error': str(e)}

result
